import React, { useState} from 'react'
import { products } from '../../constants/products';
import './styles.css';
import Card from './Card';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import DeleteIcon from '@material-ui/icons/Delete';
import CloseIcon from '@material-ui/icons/Close';

const Cards = () => {

    const [cart, setCart] = useState([]);
    console.log(cart);

    const [showCart, setShowCart] = useState(false);
    console.log(showCart);

    const handleShowCart = (e) => {
        e.preventDefault();
        if(showCart === false) {
            setShowCart(true);
        } else {
            setShowCart(false);
        }
    
    }

    const addToCart = (product) => {
        setCart([...cart, product]);
    }

    const Modal = () => {
        return (
            <div className='modal' onClick={(e) => handleShowCart(e)}>
                <div className='modal-container'>
                    <h2> <ShoppingCartIcon /> {cart.length ? 'Carrito' : 'Carrito vacío ☹️'}</h2>
                    <CloseIcon style={{ position: 'absolute', right: '33%', top: '6.7rem' }} />
                    { cart.length ? cart.map((cartItem) => (
                        <div key={cartItem.id} className='cart-item'>
                            <img src={ cartItem?.imageCart} alt='cart-item' />
                            <h3>{cartItem?.title}</h3>
                            <span>${cartItem?.price}</span>
                            <DeleteIcon style={{ marginRight: '.5rem' }} />
                        </div>
                    )) : 
                        <h2 style={{ marginTop: '2.5rem', marginLeft: 'auto', marginRight: 'auto', textAlign: 'center'}} >
                        Todavía no agregaste nada al carrito ☹️
                        </h2>
                    }
                </div>
            </div>
        )
    }

    return (
        <div className='container-cards'>
            <div className='shop-cart' onClick={(e) => handleShowCart(e)}>
                <div className='number-cart'><span>{cart.length}</span></div> 
                <ShoppingCartIcon />
            </div>
            { showCart ? <Modal /> : null }
            {products.map(({ id, images, title, description, category, price, talle, alto, ancho, stock }) => (
                <Card   images={images} title={title} description={description} 
                        category={category} key={id} price={price} talle={talle} 
                        alto={alto} ancho={ancho} stock={stock} cart={cart} addToCart={addToCart}
                />
            ))}
        </div>
    )
}

export default Cards
