import React, { useState} from 'react'
import { products } from '../../constants/products';
import './styles.css';
import Card from './Card';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';

const Cards = () => {

    const [cart, setCart] = useState([]);
    console.log(cart);

    const [showModal, setShowModal] = useState(false);

    const addToCart = (product) => {
        setCart([...cart, product]);
    }

    const Modal = () => {
        <div className='modal-container'>
            {cart.map((cartItem) => (
                <div key={cartItem.id}>
                    { cartItem?.image}
                    {cartItem?.title}
                    {cartItem?.price}
                </div>
            ))}
        </div>
    }

    const handleShowModal = () => {
        setShowModal(true);
    }

    return (
        <div className='container-cards'>
            <div className='shop-cart'>
                <div className='number-cart'><span>{cart.length}</span></div> 
                <ShoppingCartIcon onClick={() => handleShowModal} />
            </div>
            {products.map(({ id, images, title, description, category, price, talle, alto, ancho, stock }) => (
                <Card   images={images} title={title} description={description} 
                        category={category} key={id} price={price} talle={talle} 
                        alto={alto} ancho={ancho} stock={stock} cart={cart} addToCart={addToCart}
                />
            ))}
            <Modal />
        </div>
    )
}

export default Cards
