import React, { useState} from 'react'
import { products } from '../../constants/products';
import './styles.css';
import Card from './Card';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';

const Cards = () => {

    const [cart, setCart] = useState([]);
    console.log(cart);

    const [showCart, setShowCart] = useState(false);
    console.log(showCart);

    const handleShowCart = (e) => {
        e.preventDefault();
        setShowCart(false);
    }

    const addToCart = (product) => {
        setCart([...cart, product]);
    }

    const Modal = () => {
        return (
        <div className='modal'>
            <div className='modal-container'>
                { cart.length ? cart.map((cartItem) => (
                    <div key={cartItem.id}>
                        <img src={ cartItem?.imageCart} alt='cart-item' />
                        {cartItem?.title}
                        {cartItem?.price}
                    </div>
                )) : 'Todavía no agregaste nada al carrito :('}
            </div>
        </div>
        )
    }

    return (
        <div className='container-cards'>
            <div className='shop-cart' onClick={(e) => handleShowCart(e)}>
                <div className='number-cart'><span>{cart.length}</span></div> 
                <ShoppingCartIcon />
            </div>
            { showCart ? <Modal /> : null }
            {products.map(({ id, images, title, description, category, price, talle, alto, ancho, stock }) => (
                <Card   images={images} title={title} description={description} 
                        category={category} key={id} price={price} talle={talle} 
                        alto={alto} ancho={ancho} stock={stock} cart={cart} addToCart={addToCart}
                />
            ))}
        </div>
    )
}

export default Cards
